import { createApp } from './main'

export default context => {
  return new Promise((resolve, reject) => {
    const { app, router, store } = createApp(context)

    const meta = app.$meta()

    router.push(context.url)

    context.meta = meta

    router.onReady(() => {
      const matchedComponents = router.getMatchedComponents()
      if (!matchedComponents.length) {
        return reject({ code: 404 })
      }

      context.rendered = () => {
        context.state = store.state
      }
      resolve(app)
    }, reject)
  })
}

import Vue from 'vue'
import Vuex from 'vuex'
import Meta from 'vue-meta'
import App from './App.vue'
import { createRouter } from './router'
import createStore from './store'

Vue.use(Vuex)
Vue.use(Meta, {
  ssrAppId: 1 // https://vue-meta.nuxtjs.org/guide/caveats.html#duplicated-tags-after-hydration-with-ssr
})

export const createApp = context => {
  const router = createRouter()
  const store = createStore(context.state)

  const app = new Vue({
    store,
    router,
    render: h => h(App)
  })
  return {
    app,
    store,
    router
  }
}

import Vue from 'vue'
import Router from 'vue-router'
import About from '../modules/about/index.vue'
import Home from '../modules/home/index.vue'

Vue.use(Router)

export function createRouter () {
  return new Router({
    mode: 'history',
    routes: [
      {
        path: '/',
        component: Home,
        name: 'home'
      },
      {
        path: '/about',
        component: About,
        name: 'about'
      }
    ]
  })
}

export const setUsers = (state, users) => {
  state.users = users
}

import axios from 'axios'

const instance = axios.create({
  baseURL: `http://localhost:${process.env.PORT || 3000}/` // should be set based on env
})

const getUsers = () => instance.get('/users')

export default {
  getUsers
}
